package JavaBasic;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numUno = 25;
		int numDos = 3;
		
		System.out.println("Declara dos variables numéricas, muestra por consola la suma, resta, multiplicación, división.");
		System.out.println();
		System.out.println("Los valores son "+numUno+" y "+numDos);
		System.out.println();
		System.out.println("La suma es: " + (numUno + numDos));
		//System.out.println(numUno + numDos);
		System.out.println("La resta es: " + (numUno - numDos));		
		//System.out.println(numUno - numDos);
		System.out.println("La multiplicación es: " + (numUno * numDos));
		//System.out.println(numUno * numDos);
		System.out.println("La división es: " + (numUno / numDos));
		//System.out.println(numUno / numDos);
		System.out.println("El módulo/resto es: " + (numUno % numDos));
		//System.out.println(numUno % numDos);

	}

}
